sp_widget.client_script:
api.controller=function($scope,spUtil,spModal) {
	/* widget controller */
	var c = this;
	console.log("hello option", c.options)
	c.onSelectItem = function(idx) {
		var id = c.data.notes[idx].sys_id;
		console.log('Note ID: ' + id);
		$rootScope.$emit('selectNote', id);
	}
	c.confirmDelete = function(sys_id){
		spModal.confirm("Are you sure you want to delete this Note record?").then(function(confirmed){
			if(confirmed){
				c.server.get({
					action: 'deleteNote',
					noteID: sys_id
				}).then( function(r){
					c.data.notes=c.data.notes.filter(function(note){
						return note.sys_id!=sys_id
					})
					spUtil.addInfoMessage("Successfully note deleted.")
				})
			}
		});
	}
	$rootScope.$on('updatedNote',function(event,data){
		console.log("hello updatedNote",data)
		//c.data.notes=c.data.notes.map(function(note){
		//	if(note.sys_id == data.noteID){
		//		note.title=data.noteTitle
		//		note.note=data.noteBody
		//	}
		//	return note
		//})
	})
	spUtil.recordWatch($scope, "x_snc_createnotes_note", "numberANYTHING", function(name) {
		console.log(name.data.operation); 
		console.log(name); 
		if(name.data.operation == "update"){
			c.server.refresh();
		}
	});
};

sp_widget.script:
(function() {
  data.notes = [];
	var noteGR = new GlideRecord('x_snc_createnotes_note');
	noteGR.addQuery('user', gs.getUser().getID());
	noteGR.orderByDesc('sys_created_on');
	noteGR.setLimit(options.max_record);
	noteGR.query();
	while (noteGR.next()) {
		var noteObj = {};
		//use service portal helper method to get some display values
		$sp.getRecordDisplayValues(noteObj, noteGR, 'number,title,sys_id');
		//get the first 20 characters of the description
		noteObj.note = noteGR.getValue('note').slice(0,20);
		//push the populated obj into the array
		data.notes.push(noteObj);
	}

	if (input && input.noteID) {
		var note = new GlideRecord('x_snc_createnotes_note');
		if (note.get(input.noteID)) {
			if (input.action == 'deleteNote') {
				note.deleteRecord(); 
			}
		}
	}

})();
